            })
        });
        // this.props.getCityData(this.props.userCityId);
    }

    removeDataHandler = (id) => {
        this.props.removeCityData(id);
    }

    render() {
        let listUserCity = null;

        if (this.props.listCity == '') {
            listUserCity = (
                <div className="addAccount">
                    <h2 className="secondary__heading">Please add your city</h2>
                </div>
            )
        } else {
            listUserCity = this.props.listCity.map(city => (
                <div key={city.name}>
                    <WeatherResultItem 
                    forUser
                    cityName={city.name}
                    temp={Math.round(city.main.temp)}
                    tempMin={city.main.temp_min}
                    tempMax={city.main.temp_max}
                    humidity={city.main.humidity}
                    windSpeed={city.wind.speed}
                    windDeg={city.wind.deg}
                    remove={() => this.removeDataHandler(city.id)}
                    description={city.weather[0].main}/>
                </div>
            ));  
        }
        return (
            <Aux>
                <div className="row col-6-of-12">
                    <h1 className="primary__heading title">Save weather for your adventures</h1>
                </div>
                {this.props.userCityId.length !== 0 && <h2 className="secondary__heading" style={{marginTop: '6rem'}}>Your saved cities</h2>}   
                {listUserCity}
            </Aux>
        )
    }
}

const mapStateToProps = state => {
    return {
        userCityId: state.user.idList,
        listCity: state.user.listCity
    }
}

const mapDispatchToProps = dispatch => {
    return {
        getCityData: (id) => dispatch(actionCreators.getCityData(id)),
        removeCityData: (id) => dispatch(actionCreators.removeCityData(id))
    }
}

const condition = authUser => !!authUser;

export default withAuthorization(condition)(withFirebase((connect(mapStateToProps, mapDispatchToProps)(MyCities))));
