import React, { Component } from 'react';
import Input from '../../UI/Input/Input';
import Button from '../../UI/Button/Button';
import { updatedObject } from '../../shared/utility';
import { withFirebase } from '../Firebase';

const INITIAL_STATE = {
    email: {
        elementType: 'input',
        elementConfig: {
            type: 'email',
            placeholder: 'Email'
        },
        value: '',
        touched: false
    },
    isFormValid: false
}

class ResetPassword extends Component {
    state = {...INITIAL_STATE};

    onChangedHandler = (event, element) => {
        const updatedElement = updatedObject(this.state[element], {value: event.target.value, touched: true});
        this.setState({[element]: updatedElement});
    }

    onSubmitHandler = e => {
        const email = this.state.email.value;
        e.preventDefault();
        this.props.firebase
            .doPasswordReset(email)

    }

    render() {
        return (
           <div>
               <form onSubmit={this.onSubmitHandler}>
                   {Object.entries(this.state).map(el => {
                       return <Input 
                                elementtype={el[1].elementType}
                                elementconfig={el[1].elementConfig}
                                value={el[1].value}
                                changed={event => this.onChangedHandler(event, el[0])}/>
                   })} 
                    <Button disabled={!this.state.isFormValid} btnType='Btn-primary'>Submit</Button>
               </form>
           </div> 
        )
    }
}

export default withFirebase(ResetPassword);
